import React, { useContext, useReducer, createContext } from 'react';
export const ColorContext = createContext({});
export const ColorStore = props => {
  return (
    <div>
      <ColorContext.Provider value={{ color: 'blue' }}>
        {props.children}
      </ColorContext.Provider>
    </div>
  )
}
