import React, { useState } from 'react';
export default () => {
  const [count, setCount] = useState(() => {
    const obj = {};
    obj.name = 'shouri';
    obj.age = 23;
    return obj;
  });
  return <div>
    {count.name}
    <button onClick={() => setCount(x => x + 1)}>+1</button>
  </div>

}
